import React, { useState, useRef, useEffect, useCallback } from 'react';
import './AvlTree.scss';
import BinaryTreeDisplay from '../BinaryTree/BinaryTreeDisplay.js';
import Element from '../Element/Element.js';

//class for node in binary tree
class BinaryTreeNode {
    constructor(value) {
        this.value = value;
        this.left = null;
        this.right = null;
    }

}

function getHeight(node) {
    if (node !== null) {
        if (getHeight(node.left) > getHeight(node.right)) {
            return 1 + getHeight(node.left);
        }
        else return 1 + getHeight(node.right);
    }
    else return 0;
}

function getBalance(node) {
    if (node !== null) {
        return getHeight(node.right) - getHeight(node.left);
    }
} 

function rotateRight(node, tree) {
    var newRoot = node.left;
    if (node === tree.root) tree.root = newRoot;
    var T2 = newRoot.right;

    newRoot.right = node;
    node.left = T2;
    
    
    //tree.root = newRoot;
}

function rotateLeft(node, tree) {
    var newRoot = node.right;
    if (node === tree.root) tree.root = newRoot;
    var T2 = newRoot.left;
    newRoot.left = node;
    node.right = T2;
}

//class for binary search tree
class AvlTreeClass {
    constructor() {
        this.root = null;
        this.height = 0;
    }

    insertWithRot(value) {
        var current = this.root;
        var parent = null;
        var height = 1;
        while (current != null) {
            if (value < current.value) {
                parent = current;
                current = current.left;
                height++;
            } else if (value > current.value) {
                parent = current;
                current = current.right;
                height++;
            } else
                return;
        }
        if (parent === null)
            this.root = new BinaryTreeNode(value);
        else if (value < parent.value)
            parent.left = new BinaryTreeNode(value);
        else
            parent.right = new BinaryTreeNode(value);
        if (height > this.height)
            this.height = height;

        //do rotations
    }

    insert(value) {
        var current = this.root;
        var parent = null;
        var height = 1;
        while (current != null) {
            if (value < current.value) {
                parent = current;
                current = current.left;
                height++;
            } else if (value > current.value) {
                parent = current;
                current = current.right;
                height++;
            } else
                return;
        }
        if (parent === null)
            this.root = new BinaryTreeNode(value);
        else if (value < parent.value)
            parent.left = new BinaryTreeNode(value);
        else
            parent.right = new BinaryTreeNode(value);
        if (height > this.height)
            this.height = height;
    }

    remove(value) {
        var current = this.root;
        var parent = null;
        //find a node equal to value else exit function
        while (true) {
            if (current === null)
                return;
            if (value < current.value) {
                parent = current;
                current = current.left;
            } else if (value > current.value) {
                parent = current;
                current = current.right;
            } else
                break;
        }
        //if node has no children set parent child to null
        if (current.left === null && current.right === null) {
            if (parent === null)
                this.root = null;
            else if (value < parent.value)
                parent.left = null;
            else
                parent.right = null;
        }
        //if node has 1 child set parent child to current child
        else if (current.right === null) {
            if (parent === null)
                this.root = current.left;
            else if (value < parent.value)
                parent.left = current.left;
            else
                parent.right = current.left;
        }
        else if (current.left === null) {
            if (parent === null)
                this.root = current.right;
            else if (value < parent.value)
                parent.left = current.right;
            else
                parent.right = current.right;
        }
        //if node has 2 children set parent child to leftmost node of right subtree of current, and delete that
        else {
            //find the leftmost child of right subtree of current, set current to that value
            var leftmostChild = current.right;
            parent = current;
            while (leftmostChild.left !== null) {
                parent = leftmostChild;
                leftmostChild = leftmostChild.left;
            }
            current.value = leftmostChild.value;
            //delete the leftmost child of right subtree
            current = leftmostChild;
            if (current.left === null && current.right === null) {
                if (current.value < parent.value)
                    parent.left = null;
                else
                    parent.right = null;
            }
            else if (current.right === null) {
                if (current.value < parent.value)
                    parent.left = current.left;
                else
                    parent.right = current.left;
            }
            else {
                if (current.value < parent.value)
                    parent.left = current.right;
                else
                    parent.right = current.right;
            }
        }
    }

    treeHeight() {
        return getHeight(this.root);
    }

}

